package kz.altyn.komek.adapters;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import kz.altyn.komek.R;import kz.altyn.komek.models.Donation;import java.util.List;public class DonationAdapter extends RecyclerView.Adapter<DonationAdapter.MyViewHolder> {    private List<Donation> donationList;    private Context context;    public DonationAdapter(List<Donation> donationList, Context context) {        this.donationList = donationList;        this.context = context;    }    @NonNull    @Override    public MyViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_donation, parent, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(MyViewHolder viewHolder, final int position) {        viewHolder.nameTV.setText(donationList.get(position).getDonorName());        viewHolder.desTv.setText(donationList.get(position).getSummary());        viewHolder.numberTv.setText(donationList.get(position).getDonorNum());        viewHolder.addressTv.setText(donationList.get(position).getDonorAddress());    }    @Override    public int getItemCount() {        return donationList.size();    }    public class MyViewHolder extends RecyclerView.ViewHolder {        TextView nameTV;        TextView addressTv;        TextView numberTv;        TextView desTv;        public MyViewHolder(View itemView) {            super(itemView);            nameTV = itemView.findViewById(R.id.titleCharity);            desTv = itemView.findViewById(R.id.addressCha5);            addressTv = itemView.findViewById(R.id.addressCharity);            numberTv = itemView.findViewById(R.id.descriptionCharity);        }    }}