package kz.altyn.komek.adapters;import android.content.Context;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import androidx.cardview.widget.CardView;import androidx.recyclerview.widget.RecyclerView;import kz.altyn.komek.R;import kz.altyn.komek.models.Package;import kz.altyn.komek.ui.activities.CharitiesList;import java.util.List;public class PackageAdapter extends RecyclerView.Adapter<PackageAdapter.MyViewHolder> {    private final List<Package> packageList;    public PackageAdapter(List<Package> packageList) {        this.packageList = packageList;    }    @Override    public MyViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_package, parent, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(MyViewHolder viewHolder, final int position) {        viewHolder.nameTV.setText(packageList.get(position).getName());        viewHolder.desTv.setText(packageList.get(position).getShortDes());        viewHolder.priceTv.setText("KZT " + packageList.get(position).getTotalPrice());        viewHolder.logo.setImageResource(packageList.get(position).getLogo());        viewHolder.itemView.setOnClickListener(v -> v.getContext().startActivity(new Intent(v.getContext(), CharitiesList.class).putExtra("donS", packageList.get(position))));        viewHolder.donate.setOnClickListener(v -> v.getContext().startActivity(new Intent(v.getContext(), CharitiesList.class).putExtra("donS", packageList.get(position))));    }    @Override    public int getItemCount() {        return packageList.size();    }    public class MyViewHolder extends RecyclerView.ViewHolder {        TextView nameTV, desTv, priceTv;        ImageView logo;        CardView donate;        public MyViewHolder(View itemView) {            super(itemView);            nameTV = itemView.findViewById(R.id.textView5);            desTv = itemView.findViewById(R.id.textView10);            priceTv = itemView.findViewById(R.id.textView11);            logo = itemView.findViewById(R.id.floatingActionButton3);            donate = itemView.findViewById(R.id.donate_money);        }    }}